@app.route('/player/config', methods=['GET'])
def get_configs():
    try:
        data = Data.get_configs()
        return jsonify({
            'success': True,
            'data': data
        })
    except:
        return jsonify({
            'success': False,
            'message': 'API error'
        })  

@app.route('/player/top', methods=['GET'])
def get_top_players():
    try:
        data = Data.search_top()
        return jsonify({
            'success': True,
            'data': data
        })
    except:
        return jsonify({
            'success': False,
            'message': 'API error'
        })  

@app.route('/player/search', methods=['POST'])
def get_player_search():
    content = request.json
    keyword = content['keyword']

    try:
        data = Data.search_player(keyword)
        return jsonify({
            'success': True,
            'data': data
        })
    except:
        return jsonify({
            'success': False,
            'message': 'API error'
        })  
    

@app.route('/player/stats/<id>', methods=['GET'])
def get_player_stats(id):
    try:
        player = Data.build_player_stats(id)
        return jsonify({
            'success': True,
            'data': player
        })
    except:
        return jsonify({
            'success': False,
            'message': 'API error'
        })

